import '../App.css';
import React, {useState} from 'react';

const AddTaskForm = (props) => {

  const onChangeTodoText = (event) => {
    console.log(event.target.value);
    props.setTodoText(event.target.value)
  };

  return (
        <div id="inputTask">
          <h2>新規タスクの追加</h2>
          <input 
            type="text"
            value={props.todoText}
            onChange={onChangeTodoText}
            />
          <button onClick={props.onClickAdd}>追加</button>
        </div>
    ) //return
}

const App = () => {

  const [todoList, setTodoList] = useState([]);
  const [todoText, setTodoText] = useState();

  const onClickAdd = () => {
    if (todoText === '') return;
    const todo = todoText;
    setTodoList([...todoList, todo]);
    setTodoText('');
  }

  return (
    <div className="App">
      <h1>TODOリスト</h1>
      <div className="radiobuttonArea">
        <input type="radio" name="radiobutton" value="all" checked="checked" />すべて
        <input type="radio" name="radiobutton" value="working" />作業中
        <input type="radio" name="radiobutton" value="complete" />完了
      </div> {/* radiobuttonArea */}

      <div className="viewTaskArea">
        <table>
            <thead>
                <tr>
                    <th>ID</th>
                    <th>コメント</th>
                    <th>状態</th>
                </tr>
            </thead>
            <tbody id="todoBody">
              {todoList.map((todo, index) => {
                return (
                <tr key={index+1}>
                  <td>{index+1}</td>
                  <td>{todo}</td>
                  <td>
                    <button>作業中</button>
                    <button>削除</button>
                  </td>               
                </tr>
                ); //return 
              })}

            </tbody>
        </table>
      </div>

      <AddTaskForm
        todoText={todoText} 
        setTodoText={setTodoText}
        onClickAdd={onClickAdd}
      />

    </div>
  ); //return
}

export default App;
