const App = () => {

  const [todoList, setTodoList] = React.useState([]);
  const [todoText, setTodoText] = useState();

  const onChangeTodoText = (event) => {
    console.log(event.target.value);
    setTodoText(event.target.value)
  };

  const onClickAdd = () => {
    if (todoText === '') return;
    const todo = todoText;
    setTodoList.push(todo);
    setTodoList('');

  }

  return (
    <div className="App">
      <h1>TODOリスト</h1>
      <div className="radiobuttonArea">
        <input type="radio" name="radiobutton" value="all" checked="checked" />すべて
        <input type="radio" name="radiobutton" value="working" />作業中
        <input type="radio" name="radiobutton" value="complete" />完了
      </div> {/* radiobuttonArea */}

      <div className="viewTaskArea">
        <table>
            <thead>
                <tr>
                    <th>ID</th>
                    <th>コメント</th>
                    <th>状態</th>
                </tr>
            </thead>
            <tbody id="todoBody">
              {todoList.map((todo, index) => {
                return (
                <tr>
                  <td>{index+1}</td>
                  <td>{todo}</td>
                  <td>
                    <button>作業中</button>
                    <button>削除</button>
                  </td>               
                </tr>
                ); //return 
              })}

            </tbody>
        </table>
      </div>

      {/* <Form /> */}

      <div id="inputTaskArea">
        <h2>新規タスクの追加</h2>
        <input 
          type="text"
          value={todoText}
          onChange={onChangeTodoText}
        />
        <button onClick={onClickAdd}>追加</button>
      </div>

    </div>
  ); //return
}

export default App;
